"use strict";

var gulp = require('gulp'),
    jasmine = require('gulp-jasmine');

gulp.task('test', function(){
    gulp.src(['**/*.spec.js', '!node_modules/**'])
        .pipe(jasmine({verbose:true, includeStackTrace: true}))
});

gulp.task('default', ['test']);
