"use strict";

var express = require('express'),
    routes = require('./bootstrap-routes'),
    path = require('path'),
    bootstrapPath = require.resolve("bootstrap"),
    bootstrapPathSuffix = '../../../dist',
    bootstrapRouter = express.Router();


routes.forEach(function(urlPath){
    bootstrapRouter.all(urlPath, function(req, res) {
        res.sendFile(urlPath, {root: path.join(bootstrapPath, bootstrapPathSuffix)}, function(err){
            if (err) {
                res.status(404).end();
            }
        });
    });
});

module.exports = bootstrapRouter;