"use strict";

var request = require('supertest'),
    app = require('./test-server'),
    routes = require('./bootstrap-routes');


describe('bootstrap-router testing', function(){

    it('an incorrect path should return 404', function(done){
        request(app)
            .get('/not-there')
            .expect(404)
            .end(function(err) {
                if (err) {
                    done.fail(err);
                } else {
                    done();
                }
            });
    });

    routes.forEach(function(path){
        it('"' + path + '" should return 200', function(done){
            request(app)
                .get('/bootstrap' + path)
                .expect(200)
                .end(function(err){
                    if (err) {
                        done.fail(err);
                    } else {
                        done();
                    }
                });
        });
    });

});